    Global.Trait('lively.morphic.DragMoveTrait').applyTo(this, {override: ['onDrag','onDragStart', 'onDragEnd']});
    this.flap = aFlap || this.get('FlapBody') || this.owner;
    this.addScript(function onDrag(evt) {
        /// OVERWRITTEN IN SETUP
        if (!(this.owner && this.owner.isActive)) return false;
        var pos = evt.getPositionIn(this.owner).subPt(this.dragOffset);
        if (this.direction === 'x') {
            this.setPosition(pt(pos.x, this.getPosition().x));
        } else {
            this.moveBy(pos.subPt(this.getPosition()));
        }
        evt.stop(); return true;
    })
    this.addScript(function morphMenuItems() {
        /// OVERWRITTEN IN SETUP
        var items = $super();
        var menuFunctionNames = ['top', 'right', 'bottom', 'left'];
        return items.concat([['Move flap to', menuFunctionNames.collect(function(functionName) {
            return [functionName, function() {
                if (!this.owner.isActive) {
                    this.owner.setup();
