        var offsetX = Math.abs(evt.getPositionIn(this).x - this.mouseDownPosition.x),
            offsetY = Math.abs(evt.getPositionIn(this).y - this.mouseDownPosition.y);
        this.direction = offsetX > offsetY ? 'x' : 'y';
        this.startDragPos = this.getPosition();
        this.dragOffset = evt.getPositionIn(this);
        delete this.mouseDownPosition;
        evt.stop(); return true;
    })
}

