function fitToWindow() {
    var pos = this.getPosition(),
        contentOffset = this.getBorder();
    this.setFixed(false);
    var offset = pos.subPt(this.getPosition());
    var align = this.getAlign(),
        maxExtrude = .5,
        vb = $world.visibleBounds(),
        rotation, extent;
    switch (align) {
        case 'top' : {
            rotation = 0 * Math.PI;
            extent = pt(vb.width, vb.height * maxExtrude);
            break;
        };
        case 'right' : {
            rotation = 0.5 * Math.PI;
            extent = pt(vb.height, vb.width * maxExtrude);
            break;
        };
        case 'bottom' : {
            rotation = 1 * Math.PI;
            extent = pt(vb.width, vb.height * maxExtrude);
            break;
        };
        case 'left' : {
            rotation = 1.5 * Math.PI;
            extent = pt(vb.height, vb.width * maxExtrude);
            break;
        }
    }
    this.setExtent(extent);
    this.setRotation(rotation);
    this.handle.setPosition(pt(0,this.getExtent().y));
    this.moveBy(offset.negated());
    this.content.setRotation((2*Math.PI)-rotation);
    this.setFixed(true);
    this.fitContent();
    this.extrude();
}

