function moveWithHandle(aDist) {
    if (this.isActive) {
        var pos = this.getPosition();
        var offset = pos.subPt(this.getPosition());
        switch(this.getAlign()) {
            case 'top': {
                this.setRestrictedPosition(pos.addPt(aDist))
                break;
            }
            case 'right': {
                this.setRestrictedPosition(pos.addPt(pt(-aDist.y, aDist.x)))
                break;
            }
            case 'bottom': {
                this.setRestrictedPosition(pos.addPt(aDist.negated()))
                break;
            }
            case 'left': {
                this.setRestrictedPosition(pos.addPt(pt(aDist.y, -aDist.x)))
                break;
            }
        }
        this.moveBy(offset.negated());
        if (this.extrusion < 0.1) {
            this.extruded = false
        }
    } else {
        this.moveBy(aDist.negated())
    }
}

