function setRestrictedPosition(aPos, optDoExclude) {
    var bounds = $world.visibleBounds(),
        align = this.getAlign();
    switch(align) {
        case 'top': {
            var maxY = bounds.top(),
                minY = bounds.top() - this.getExtent().y,
                maxX = bounds.left(),
                minX = bounds.left();
            break;
        }
        case 'right': {
            var maxY = bounds.top(),
                minY = bounds.top(),
                maxX = bounds.right() + this.getExtent().y,
                minX = bounds.right();
            break;
        }
        case 'bottom': {
            var maxY = bounds.bottom() + this.getExtent().y,
                minY = bounds.bottom(),
                maxX = bounds.right(),
                minX = bounds.right()
            break;
        }
        case 'left': {
            var maxY = bounds.bottom(),
                minY = bounds.bottom(),
                maxX = bounds.left(),
                minX = bounds.left() - this.getExtent().y;
            break;
        }
    }
    if (aPos.y < minY) { aPos.y = Math.max(aPos.y, minY); };
    if (aPos.y > maxY) { aPos.y = Math.min(aPos.y, maxY); };
    if (aPos.x < minX) { aPos.x = Math.max(aPos.x, minX); };
    if (aPos.x > maxX) { aPos.x = Math.min(aPos.x, maxX); };
    if (optDoExclude !== false) {
        this.setExtrusion(
            align === 'top' ? 1-((maxY-aPos.y)/(maxY-minY)) :
            align === 'right' ? (maxX-aPos.x)/(maxX-minX) :
            align === 'bottom' ? (maxY-aPos.y)/(maxY-minY) :
            align === 'left' ? 1-((maxX-aPos.x)/(maxX-minX)):
            undefined);
    }
    this.setPosition(aPos);
}

