function extrude(optVal) {
    if (!this.isActive) { return; };
    var extrusion = typeof(optVal) === 'undefined' ?  this.extrusion : optVal;
    var align = this.getAlign(),
        vb = $world.visibleBounds(), position;
    switch (align) {
        case 'top' : {
            position = pt(vb.topLeft().x, vb.topLeft().y - (this.getExtent().y * (1- extrusion)));
            break;
        };
        case 'right' : {
            position = pt(vb.topRight().x + (this.getExtent().y * (1-extrusion)), vb.topRight().y);
            break;
        };
        case 'bottom' : {
            position = pt(vb.bottomRight().x, vb.bottomRight().y + (this.getExtent().y * (1-extrusion)));
            break;
        };
        case 'left' : {
            position = pt(vb.bottomLeft().x - (this.getExtent().y * (1-extrusion)), vb.bottomLeft().y);
            break;
        }
    }
    this.withCSSTransitionDo(function() {
        this.setRestrictedPosition(position, false);
    }, 300)
}

