function onDropOn(aMorph) {
    var handPosition = (this.getPosition().subPt(this.offset))
    var top = handPosition.y - $world.visibleBounds().top(),
        right = $world.visibleBounds().right() - handPosition.x,
        bottom = $world.visibleBounds().bottom() - handPosition.y,
        left = handPosition.x - $world.visibleBounds().left(),
        centerX = Math.abs(handPosition.x - $world.visibleBounds().center().x),
        centerY = Math.abs(handPosition.y - $world.visibleBounds().center().y);
    var offsetX = Math.min(centerX, right, left),
        offsetY = Math.min(centerY, top, bottom),
        offsetBorder = Math.min(top, right, bottom, left),
        offsetBorderX = Math.min(right, left),
        offsetBorderY = Math.min(top, bottom);
    if (offsetBorderX > offsetX &&  offsetBorderX === offsetBorder || 
        offsetBorderY > offsetY &&  offsetBorderY === offsetBorder) {
        this.reset()
    } else {
        this.setAlign(  offsetBorder === top ? 'top' :
                        offsetBorder === right ? 'right' :
                        offsetBorder === bottom ? 'bottom' :
                        offsetBorder === left ? 'left' : 'top');
        this.setup();
    }
}

