function fitContent() {
    var contentOffset = this.getBorder(),
        offset = this.getPosition().subPt(this.getPosition()),
        extent = this.getExtent(),
        contentExtent, contentPosition;
    switch (this.getAlign()) {
        case 'top' : {
            contentExtent = extent.subPt(pt(2*contentOffset, 2*contentOffset));
            contentPosition = pt(contentOffset, contentOffset);
            break;
        };
        case 'right' : {
            contentExtent = pt(extent.y - (2*contentOffset),
                extent.x - (2*contentOffset));
            contentPosition = pt(contentOffset, extent.y - contentOffset);
            break;
        };
        case 'bottom' : {
            contentExtent = extent.subPt(pt(2*contentOffset, 2*contentOffset));
            contentPosition = pt(extent.x - contentOffset, extent.y - contentOffset);
            break;
        };
        case 'left' : {
            contentExtent = pt(extent.y - (2*contentOffset),
                extent.x - (2*contentOffset));
            contentPosition = pt(extent.x - contentOffset, contentOffset);
            break;
        }
    }
    this.content.setRotation((2*Math.PI)-this.getRotation());
    this.content.setExtent(contentExtent);
    this.content.setPosition(contentPosition);
}
